import React, { useState, useEffect } from "react";
import courseStore from "../stores/courseStore";
import CourseList from "../components/CourseList";
import { Link } from "react-router-dom";
import { loadCourses, deleteCourse } from "../actions/courseActions";

import Table from "./Table";
function CoursePage() {
  const [courses, setCourses] = useState(courseStore.getCourse());

  useEffect(() => {
    courseStore.addChangeListner(onChange);
    if (courseStore.getCourse().length === 0) loadCourses();
    return () => courseStore.removeChangeListner(onChange); //cleanup on unmount
  }, []);

  function onChange() {
    setCourses(courseStore.getCourse());
  }

  return (
    <>
      <h3>Courses</h3>
      <Link className="btn btn-primary" to="/course">
        Add Course
      </Link>
      <Table courses={courses} />
      <CourseList courses={courses} deleteCourse={deleteCourse} />
    </>
  );
}

export default CoursePage;
