import React from "react"
import {getCourses} from "./api/courseApi"

class CoursePage extends React.Component{
    state = {
        courses : []
    }
    componentDidMount(){
        getCourses().then(courses => this.setState({courses: courses}));
    }
    return(){
        return <h3>Courses</h3>
    }

}
export default CoursePage
