import React, { useState, useEffect } from "react";
import courseStore from "../stores/courseStore";
import CourseList from "../components/CourseList";
import { Link } from "react-router-dom";
import { loadCourses } from "../actions/courseActions";
import ReactTable from "react-table";

function CoursePage() {
  const [courses, setCourses] = useState(courseStore.getCourse());
  const data = [{
    name: 'Roy Agasthyan',
    age: 26
  },{
    name: 'Sam Thomason',
    age: 22
  },{
    name: 'Michael Jackson',
    age: 36
  },{
    name: 'Samuel Roy',
    age: 56
  },{
    name: 'Rima Soy',
    age: 28
  },{
    name: 'Suzi Eliamma',
    age: 28
  }]

  const columns = [{
    Header: 'Name',
    accessor: 'name'
  },{
    Header: 'Age',
    accessor: 'age'
  }]
  useEffect(() => {
    courseStore.addChangeListner(onChange);
    if (courseStore.getCourse().length === 0) loadCourses();
    return () => courseStore.removeChangeListner(onChange); //cleanup on unmount
  }, []);

  function onChange() {
    setCourses(courseStore.getCourse());
  }

  return (
    <>
      <h3>Courses</h3>
      <Link className="btn btn-primary" to="/course">
        Add Course
      </Link>
      <ReactTable
                data={data}
                columns={columns}
                defaultPageSize = {3}
                pageSizeOptions = {[3, 6]}
              />
      <CourseList courses={courses} />
    </>
  );
}

export default CoursePage;
