import React from "react";
import { getCourses } from "../api/courseApi";

class Course extends React.Component {
  state = {
    courses: []
  };
  componentDidMount() {
    getCourses().then(courses => this.setState({ courses: courses }));
  }
  render() {
    return <h3>Courses</h3>;
  }
}

export default Course;
