import dispatcher from "../appDispatcher";
import * as CourseAPI from "../api/courseApi";

import ActionType from "./actionTypes";

export function SaveCourse(course) {
  return CourseAPI.saveCourse(course).then((savedCourse) => {
    // dispatcher informs all the stores that a course is created
    dispatcher.dispatch({
      actionType: course.id
        ? ActionType.UPDATE_COURSE
        : ActionType.CREATE_COURSE,
      course: savedCourse,
    });
  });
}
export function loadCourses() {
  return CourseAPI.getCourses().then((courses) => {
    // dispatcher informs all the stores that a courses to be loaded
    dispatcher.dispatch({
      actionType: ActionType.LOAD_COURSES,
      courses: courses,
    });
  });
}
export function deleteCourse(id) {
  return CourseAPI.deleteCourse(id).then(() => {
    // dispatcher informs all the stores that a course is deleted
    dispatcher.dispatch({
      actionType: ActionType.DELETE_COURSE,
      id: id,
    });
  });
}
